import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link } from 'react-router-dom';
import '../components/MobileDemo.css';

interface FormData {
  fullName: string;
  email: string;
  yearOfStudy: string;
  isBetaTester: boolean;
  referralCode: string;
  goals: string[];
  studyMethods: string[];
  struggles: string[];
  otherGoals: string;
  otherStruggles: string;
}

const HomePage: React.FC = () => {
  const [formData, setFormData] = useState<FormData>({
    fullName: '',
    email: '',
    yearOfStudy: '',
    isBetaTester: false,
    referralCode: '',
    goals: [],
    studyMethods: [],
    struggles: [],
    otherGoals: '',
    otherStruggles: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [userReferralCode, setUserReferralCode] = useState('');

  // Mobile Demo Carousel State
  const [currentDemoStep, setCurrentDemoStep] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);
  const [touchStart, setTouchStart] = useState(0);
  const [touchEnd, setTouchEnd] = useState(0);
  const autoPlayRef = useRef<NodeJS.Timeout | null>(null);

  // Medical Terms Rotation State
  const [currentTermIndex, setCurrentTermIndex] = useState(0);
  const medicalTerms = [
    {
      term: "Disseminated Intravascular Coagulation (DIC)",
      explanation: "A rare condition where blood clots form all over the body and then cause severe bleeding."
    },
    {
      term: "Hypertrophic Cardiomyopathy",
      explanation: "A heart disease where the muscle wall becomes too thick, making it harder to pump blood."
    },
    {
      term: "Glioblastoma Multiforme",
      explanation: "An aggressive brain tumor that grows and spreads quickly."
    },
    {
      term: "Pulmonary Alveolar Proteinosis",
      explanation: "A lung disease where protein-like material builds up, making it hard to breathe."
    },
    {
      term: "Systemic Lupus Erythematosus (SLE)",
      explanation: "A long-term autoimmune disease where the immune system attacks the body's own tissues."
    }
  ];

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target;
    if (type === 'checkbox') {
      const checked = (e.target as HTMLInputElement).checked;
      setFormData(prev => ({
        ...prev,
        [name]: checked
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleCheckboxChange = (field: 'goals' | 'studyMethods' | 'struggles', value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: prev[field].includes(value)
        ? prev[field].filter(item => item !== value)
        : [...prev[field], value]
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('http://localhost:5001/api/waitlist/join', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setUserReferralCode(data.referralCode);
        setSubmitted(true);
        setFormData({
          fullName: '',
          email: '',
          yearOfStudy: '',
          isBetaTester: false,
          referralCode: '',
          goals: [],
          studyMethods: [],
          struggles: [],
          otherGoals: '',
          otherStruggles: ''
        });
      } else {
        alert(data.message || 'Error submitting form');
      }
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('Error submitting form');
    } finally {
      setIsSubmitting(false);
    }
  };

  const scrollToWaitlist = () => {
    const waitlistSection = document.getElementById('waitlist-section');
    if (waitlistSection) {
      waitlistSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Mobile Demo Functions
  const nextDemoStep = () => {
    setCurrentDemoStep((prev) => (prev + 1) % 4);
  };

  const prevDemoStep = () => {
    setCurrentDemoStep((prev) => (prev - 1 + 4) % 4);
  };

  const handleManualControl = () => {
    setIsAutoPlaying(false);
    if (autoPlayRef.current) {
      clearTimeout(autoPlayRef.current);
    }
    // Resume auto-play after 5 seconds
    setTimeout(() => {
      setIsAutoPlaying(true);
    }, 5000);
  };

  const handleTouchStart = (e: React.TouchEvent) => {
    setTouchStart(e.targetTouches[0].clientX);
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    setTouchEnd(e.targetTouches[0].clientX);
  };

  const handleTouchEnd = () => {
    if (!touchStart || !touchEnd) return;

    const distance = touchStart - touchEnd;
    const isLeftSwipe = distance > 50;
    const isRightSwipe = distance < -50;

    if (isLeftSwipe) {
      nextDemoStep();
      handleManualControl();
    }
    if (isRightSwipe) {
      prevDemoStep();
      handleManualControl();
    }
  };

  // Medical Terms Rotation
  const nextMedicalTerm = useCallback(() => {
    setCurrentTermIndex((prev) => (prev + 1) % medicalTerms.length);
  }, [medicalTerms.length]);

  // Auto-play effects
  useEffect(() => {
    if (isAutoPlaying) {
      autoPlayRef.current = setInterval(nextDemoStep, 2000);
    } else {
      if (autoPlayRef.current) {
        clearInterval(autoPlayRef.current);
      }
    }
    return () => {
      if (autoPlayRef.current) {
        clearInterval(autoPlayRef.current);
      }
    };
  }, [isAutoPlaying, nextDemoStep]);

  useEffect(() => {
    const termInterval = setInterval(nextMedicalTerm, 3000);
    return () => clearInterval(termInterval);
  }, [nextMedicalTerm]);

  // Hash-based navigation
  useEffect(() => {
    const hash = window.location.hash;
    if (hash === '#about') {
      document.getElementById('about')?.scrollIntoView({ behavior: 'smooth' });
    } else if (hash === '#referral') {
      document.getElementById('referral')?.scrollIntoView({ behavior: 'smooth' });
    } else if (hash === '#waitlist') {
      document.getElementById('waitlist-section')?.scrollIntoView({ behavior: 'smooth' });
    }
  }, []);

  return (
    <div className="min-h-screen bg-white">
      {/* Hero Section */}
      <section className="bg-gradient-to-br from-gray-50 to-gray-100 py-16 lg:py-24">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            <div className="space-y-8">
              <h1 className="text-4xl lg:text-6xl font-bold text-gray-900 leading-tight">
                Simplify Medical Education with{' '}
                <span className="bg-gradient-to-r from-primary-500 to-secondary-500 bg-clip-text text-transparent">
                  AI-Powered Explanations
                </span>
              </h1>
              <p className="text-xl text-gray-600 leading-relaxed">
                Transform complex medical terms into simple, understandable explanations. 
                Join thousands of medical students who are already learning smarter with MedLens.
              </p>
              <div className="flex flex-col sm:flex-row gap-4">
                <button
                  onClick={scrollToWaitlist}
                  className="bg-gradient-to-r from-primary-500 to-secondary-500 text-white px-8 py-4 rounded-xl font-semibold text-lg hover:shadow-xl hover:-translate-y-1 transition-all duration-200"
                >
                  Join the Waitlist ðŸš€
                </button>
                <button
                  onClick={() => document.getElementById('about')?.scrollIntoView({ behavior: 'smooth' })}
                  className="border-2 border-primary-500 text-primary-500 px-8 py-4 rounded-xl font-semibold text-lg hover:bg-primary-50 transition-all duration-200"
                >
                  Learn More
                </button>
              </div>
            </div>

            {/* Mobile Demo Section */}
            <div className="flex justify-center">
              <div className="iphone-mockup w-80 h-[600px] rounded-[3rem] p-2">
                <div className="iphone-screen w-full h-full rounded-[2.5rem] overflow-hidden relative">
                  {/* Status Bar */}
                  <div className="iphone-status-bar absolute top-0 left-0 right-0 h-12 flex items-center justify-between px-6 z-10">
                    <span className="text-white font-semibold text-sm">9:41</span>
                    <div className="flex items-center space-x-1">
                      <div className="w-4 h-2 bg-white rounded-sm"></div>
                      <div className="w-4 h-2 bg-white rounded-sm"></div>
                      <div className="w-4 h-2 bg-white rounded-sm"></div>
                      <div className="w-6 h-3 border border-white rounded-sm flex items-center justify-center">
                        <div className="w-4 h-2 bg-white rounded-sm"></div>
                      </div>
                    </div>
                    <div className="flex items-center space-x-1">
                      <div className="w-4 h-2 bg-white rounded-sm"></div>
                      <div className="w-4 h-2 bg-white rounded-sm"></div>
                      <div className="w-4 h-2 bg-white rounded-sm"></div>
                      <div className="w-6 h-3 border border-white rounded-sm flex items-center justify-center">
                        <div className="w-4 h-2 bg-white rounded-sm"></div>
                      </div>
                    </div>
                  </div>

                  {/* Demo Content */}
                  <div className="pt-12 h-full flex items-center justify-center">
                    <div className="text-center p-6">
                      <div className="w-16 h-16 bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full mx-auto mb-4 flex items-center justify-center">
                        <span className="text-white text-2xl">ðŸ“±</span>
                      </div>
                      <h3 className="text-lg font-semibold text-gray-900 mb-2">MedLens App</h3>
                      <p className="text-gray-600 text-sm">AI-Powered Medical Learning</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Waitlist Section */}
      <section id="waitlist-section" className="py-16 bg-gradient-to-br from-gray-50 to-gray-100">
